import React from "react";
import Card from "./Card";
import styled from "styled-components";

let x = 3;
let y = 3;

function App() {
  const makeGame = (x, y) => {
    let answer = ~~(Math.random() * x * y);
    let gameArr = Array(x * y).fill("멵");
    gameArr[answer] = "먽";
    return gameArr;
  };

  return (
    <Grid>
      {makeGame(x, y).map((e) => (
        <button>
          <Card>{e}</Card>
        </button>
      ))}
    </Grid>
  );
}

const Grid = styled.article`
  display: grid;
  width: 50vw;
  grid-template-columns: repeat(${x}, 1fr);
  grid-template-rows: repeat(${y}, 1fr);
  gap: 10px;
`;
export default App;
