import React, { useState } from "react";
import Game from "./Game";
import styled from "styled-components";

function App() {
  const [x, setX] = useState(3);
  const [y, setY] = useState(3);
  const [newAnswer, setNewAnswer] = useState(~~(Math.random() * x * y));
  const [stop, setStop] = useState(true);
  const clickAnswer = (e) => {
    alert("Ï†ïÎãµÏûÖÎãàÎã§!");
    setX((prev) => prev + 1);
    setY((prev) => prev + 1);
    setNewAnswer(~~(Math.random() * x * y));
  };

  const clickWrong = (e) => {
    alert("ÌãÄÎ†∏ÏäµÎãàÎã§!");
    setStop(false);
  };

  const restart = () => {
    setX(3);
    setY(3);
  };

  return (
    <Main>
      <h1>Îã§Î•∏ Í∏ÄÏûê Ï∞æÍ∏∞</h1>
      <Game
        x={x}
        y={y}
        newAnswer={newAnswer}
        clickAnswer={clickAnswer}
        clickWrong={clickWrong}
      />
      <Stop display={stop ? "none" : "inline-block"}>
        <p>GAME OVER ü§∑</p>
        <Restart onClick={restart}>restart</Restart>
      </Stop>
    </Main>
  );
}
export default App;

const Main = styled.section`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

const Stop = styled.div`
  display: ${(props) => props.display};
  position: absolute;
  left: 0;
  top: 0;
  width: 100vw;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.2);
  font-size: 100px;
  font-weight: 700px;
  text-align: center;
  z-index: 100;
  font-family: "Walter Turncoat", cursive;
`;

const Restart = styled.button`
  width: 100px;
  height: 50px;
  font-size: 20px;
  background-color: #00acc1;
  border: none;
  border-radius: 8px;
  font-family: inherit;
  cursor: pointer;
  z-index: 110;
`;
