import React, { useState } from "react";
import styled, { keyframes, createGlobalStyle } from "styled-components";
import App from "./App";
import { Modal } from "./Modal";
import "../fonts/font.css";
import Restart from "./Restart";

const GlobalStyle = createGlobalStyle`
font-family : "SANJUGotgam"`;

function Start() {
  const [start, setStart] = useState(false);

  const gameStart = () => {
    setStart(true);
  };

  return (
    <>
      {!start ? (
        <Main>
          <GlobalStyle />
          <Title>다른 글자찾기 게임을 시작해봅시다!</Title>
          <Restart onClick={gameStart}>START!</Restart>
        </Main>
      ) : (
        <App />
      )}
      <Modal />
    </>
  );
}

const Main = styled.section`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 100vw;
  height: 100vh;
  margin: 0;
  padding: 0;
  background-color: #6a66f2;
  color: white;
`;

const Title = styled.h2`
  font-size: 42px;
  font-family: "SANJUGotgam";
`;

const clickMe = keyframes`
  from {
    box-shadow: 0 0 0 0 rgba(195, 242, 92, 0);
  }
  50% {
    box-shadow: 0 0 0 5px rgba(195, 242, 92, 0.5);
  }
  to {
    box-shadow: 0 0 0 0 rgba(195, 242, 92, 0);
  }
`;

const StartBtn = styled.button`
  width: 100px;
  height: 50px;
  font-size: 20px;
  background-color: #c3f25c;
  border: none;
  border-radius: 8px;
  font-family: inherit;
  cursor: pointer;
  z-index: 110;
  animation: ${clickMe} 1s linear infinite;
`;

export default Start;
