import styled from "styled-components";

export function Modal() {
  return (
    <Modalbox>
      <Box>정답입니다!</Box>
    </Modalbox>
  );
}

const Modalbox = styled.article`
  display: flex;
  justify-content: center;
  align-items: center;
  position: absolute;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  z-index: 1000;
  &::before {
    position: absolute;
    top: 0;
    left: 0;
    content: "";
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.2);
  }
`;

const Box = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
  width: 300px;
  height: 200px;
  background: #ffffff;
  border-radius: 30px;
`;
