import React from "react";
import Card from "./Card";

import styled from "styled-components";

let newX;
let newY;

function Game({ x, y }) {
  newX = x;
  newY = y;

  const makeGame = (x, y) => {
    let answer = ~~(Math.random() * x * y);
    let gameArr = Array(x * y).fill("멵");
    gameArr[answer] = "먽";
    return gameArr;
  };

  return (
    <Grid>
      {makeGame(x, y).map((e) => (
        <Card>{e}</Card>
      ))}
    </Grid>
  );
}

const Grid = styled.article`
  display: grid;
  width: 50vw;
  grid-template-columns: repeat(${newX}, 1fr);
  grid-template-rows: repeat(${newY}, 1fr);
  gap: 10px;
`;
export default Game;
