import React, { useState } from "react";
import Card from "./Card";

import styled from "styled-components";

function Game({ x, y }) {
  const [answer, setAnswer] = useState(~~(Math.random() * x * y));

  const makeGame = (x, y) => {
    let gameArr = Array(x * y).fill("멵");
    gameArr[answer] = "먽";
    return gameArr;
  };

  const clickAnswer = (e) => {
    alert("정답입니다!");
  };

  const clickWrong = (e) => {
    alert("틀렸습니다!");
  };

  return (
    <Grid x={x} y={y}>
      {makeGame(x, y).map((e, idx) => (
        <Card onClick={idx === answer ? clickAnswer : clickWrong} key={idx}>
          {e}
        </Card>
      ))}
    </Grid>
  );
}

const Grid = styled.article`
  display: grid;
  width: 70vw;
  grid-template-columns: repeat(${(props) => props.x}, 1fr);
  grid-template-rows: repeat(${(props) => props.y}, 1fr);
  gap: 10px;
`;
export default Game;
