import React, { useEffect, useState } from "react";
import Game from "./Game";
import styled from "styled-components";

function App() {
  const [x, setX] = useState(3);
  const [y, setY] = useState(3);
  const [newAnswer, setNewAnswer] = useState(~~(Math.random() * x * y));
  const [time, setTime] = useState(5);

  const [stop, setStop] = useState(true);
  const [level, setLevel] = useState(1);

  useEffect(() => {
    const timer = setTimeout(() => {
      setTime((prev) => prev - 1);
    }, 1000);

    if (time < 0) {
      clickWrong();
      clearTimeout(timer);
    }

    return () => clearTimeout(timer);
  });

  const clickAnswer = () => {
    alert("정답입니다!");
    setX((prev) => prev + 1);
    setY((prev) => prev + 1);
    setLevel((prev) => prev + 1);
    setNewAnswer(~~(Math.random() * x * y));
  };

  const clickWrong = () => {
    alert("틀렸습니다!");
    setStop(false);
  };

  const restart = () => {
    setX(3);
    setY(3);
    setLevel(1);
    setStop(true);
    setTime(5);
    setNewAnswer(~~(Math.random() * 9));
  };

  return (
    <Main>
      <h1>다른 른자 찾기 🔍</h1>
      <span>LV. {level}</span>
      <span>{time}</span>
      <Game
        x={x}
        y={y}
        newAnswer={newAnswer}
        clickAnswer={clickAnswer}
        clickWrong={clickWrong}
      />
      <Stop display={stop ? "none" : "flex"}>
        <Popupmsg>LV {level}</Popupmsg>
        <Popupmsg>GAME OVER 🤷</Popupmsg>
        <Restart onClick={restart}>restart</Restart>
      </Stop>
    </Main>
  );
}
export default App;

const Main = styled.section`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 100vw;
  height: 100vh;
  background-color: black;
  color: white;
`;

const Stop = styled.div`
  display: ${(props) => props.display};
  position: absolute;
  left: 0;
  top: 0;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 100vw;
  height: 100vh;
  background-color: rgba(255, 255, 255, 0.5);
  text-align: center;
  z-index: 100;
`;

const Popupmsg = styled.span`
  font-family: "Walter Turncoat", cursive;
  color: #5ddef4;
  font-size: 100px;
  animation: answer 3s infinite;

  @keyframe answer {
    from {
    }
  }
`;

const Restart = styled.button`
  width: 100px;
  height: 50px;
  font-size: 20px;
  background-color: #00acc1;
  border: none;
  border-radius: 8px;
  font-family: inherit;
  cursor: pointer;
  z-index: 110;
`;
