import React, { useState } from "react";
import Game from "./Game";
import styled from "styled-components";

function App() {
  const [x, setX] = useState(3);
  const [y, setY] = useState(3);
  const [time, setTime] = useState(5);

  const clickAnswer = (e) => {
    alert("정답입니다!");
    setX((prev) => prev + 1);
    setY((prev) => prev + 1);
  };

  const clickWrong = (e) => {
    alert("틀렸습니다!");
  };

  const timer = () => {
    if (time <= 0) clearInterval();
    setInterval(() => setTime((prev) => prev - 1), 1000);
  };

  return (
    <>
      {timer()}
      <h1>다른 글자 찾기</h1>
      <p>time : {time}</p>
      <Game x={x} y={y} clickAnswer={clickAnswer} clickWrong={clickWrong} />
    </>
  );
}
export default App;
